int	gnl(int fd, char *res)
{
	char	*line;

	line = get_next_line(fd);
	if (ft_strcmp(line, res) || (res && !line) || (line && res == NULL))
	{
		printf("%s instead of %s\n", line, res);
		if (!res)
			free(res);
		return (0);
	}
	else if (!line)
		printf ("(NULL)\n");
	else
		printf("%s\n", line);
	free (line);
	return (1);
}
